const useStyles = makeStyles((theme) => ({
  timer: {
    color: (timeLeft) => (timeLeft < 10 ? "red" : "inherit"),
  },
  box: {
    marginLeft: "auto",
    border: "1px solid black",
    padding: "0px",
    borderRadius: "5px",
    backgroundColor: "gray",
    width: "20%",
    position: "absolute",
    top: "0",
    right: "0",
  },
}), { name: 'QuizTimer' });

function QuizTimer() {
  let { id } = useParams();
  const [examDetails, setExamDetails] = useState(null);
  const [timeLeft, setTimeLeft] = useState(null);
  const [quizEnded, setQuizEnded] = useState(false);
  const classes = useStyles(timeLeft ? timeLeft : {});

  useEffect(() => {
    async function getExamDetails() {
      let detail = await axios.get(`${baseUrl}/exam/${id}`);
      setExamDetails(detail.data);
      setTimeLeft(detail.data.time * 60); // set timeLeft from examDetails
    }
    getExamDetails();
  }, [id]);

  useEffect(() => {
    if (timeLeft === 0) {
      setQuizEnded(true);
    }
  }, [timeLeft]);

  if (quizEnded) {
    return <div>Quiz ended!</div>;
  }

  return (
    <Typography
      style={{ position: "absolute", top: "0", right: "0" }}
      variant="h4"
      component="h2"
      className={classes.timer}
    >
      <h5>
        Time left: {Math.floor(timeLeft / 60)}m {timeLeft % 60}s
      </h5>
    </Typography>
  );
}

export default QuizTimer;
