import React, { useState, useEffect } from "react";
import { Typography } from "@mui/material";
import { makeStyles } from "@mui/styles";
import { useParams } from "react-router-dom";
import axios from "axios";
import baseUrl from "./baseUrl";

const useStyles = makeStyles((theme) => ({
  timer: {
    color: (timeLeft) => (timeLeft < 10 ? "red" : "inherit"),
  },
}));

function QuizTimer() {
  const { id } = useParams();
  const [examDetails, setExamDetails] = useState(null);
  const [timeLeft, setTimeLeft] = useState(null);
  const [quizEnded, setQuizEnded] = useState(false);
  const classes = useStyles(timeLeft);

  useEffect(() => {
    async function getExamDetails() {
      const response = await axios.get(`${baseUrl}/exam/${id}`);
      setExamDetails(response.data);
    }

    getExamDetails();
  }, [id]);

  useEffect(() => {
    if (examDetails) {
      setTimeLeft(examDetails.duration * 60);
    }
  }, [examDetails]);

  useEffect(() => {
    if (timeLeft === 0) {
      setQuizEnded(true);
    } else {
      const timer = setInterval(() => {
        setTimeLeft((prevTime) => prevTime - 1);
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [timeLeft]);

  if (!timeLeft || quizEnded) {
    return <div>Quiz ended!</div>;
  }

  const minutesLeft = Math.floor(timeLeft / 60);
  const secondsLeft = timeLeft % 60;

  return (
    <Typography
      style={{ position: "absolute", top: 0, right: 0 }}
      variant="h4"
      component="h2"
      className={classes.timer}
    >
      <h5>
        Time left: {minutesLeft}m {secondsLeft}s
      </h5>
    </Typography>
  );
}

export default QuizTimer;
