import React, { useState, useEffect } from "react";
import { Typography } from "@mui/material";
import { makeStyles } from "@mui/styles";
import { useParams } from "react-router-dom";
import axios from "axios";
import baseUrl from "./baseUrl";

const useStyles = makeStyles((theme) => ({
  timer: {
    color: (timeLeft) => (timeLeft < 10 ? "red" : "inherit"),
  },
}));

function QuizTimer() {
  let { id } = useParams();
  const [examDetails, setExamDetails] = useState(null);
  const [timeLeft, setTimeLeft] = useState(null);
  const [quizEnded, setQuizEnded] = useState(false);
  const classes = useStyles(timeLeft);

  useEffect(() => {
    async function getExamDetails() {
      try {
        const response = await axios.get(`${baseUrl}/exam/${id}`);
        setExamDetails(response.data);
        setTimeLeft(response.data.duration);
      } catch (error) {
        console.error(error);
      }
    }
    getExamDetails();
  }, [id]);

  useEffect(() => {
    if (timeLeft === null) return; // Don't start timer until timeLeft is set
    const timer = setInterval(() => {
      setTimeLeft((prevTime) => prevTime - 1);
    }, 1000);
    return () => clearInterval(timer);
  }, [timeLeft]);

  useEffect(() => {
    if (timeLeft === 0) {
      setQuizEnded(true);
    }
  }, [timeLeft]);

  if (quizEnded) {
    return <div>Quiz ended!</div>;
  }

  if (!examDetails || timeLeft === null) {
    return <div>Fetching exam details...</div>;
  }

  return (
    <Typography style={{ position: "absolute", top: "0", right: "0" }} variant="h4" component="h2" className={classes.timer}>
      <h5>Time left: {Math.floor(timeLeft / 60)}m {timeLeft % 60}s</h5>
    </Typography>
  );
}

export default QuizTimer;
